import{_ as p}from"./plugin-vue_export-helper.21dcd24c.js";import{o as r,c as t,e as o}from"./app.6abff923.js";const n={},e=o('<p>\u65E5\u6708\u5176\u8FC8\uFF0C\u5C81\u807F\u5176\u83AB<br><br></p><p>\u5C81\u6708\u603B\u662F\u5728\u4E0D\u7ECF\u610F\u95F4\u4ECE\u80A9\u5934\u6E9C\u8D70</p><p>\u987F\u4E0B\u811A\u6B65\uFF0C\u7A81\u7136\u53D1\u73B0\u6211\u5DF2\u7ECF\u7AD9\u5728\u4E8630\u5C81\u7684\u95E8\u53E3</p><p>\u6211\u603B\u7231\u8BF4\u7684\u4E00\u53E5\u8BDD\u662F\uFF1A<strong>\u65E0\u8BBA\u4F55\u65F6\u5F52\u6765\uFF0C\u6211\u90FD\u662F\u5C11\u5E74</strong></p><p>\u4F46\u5F53\u800C\u7ACB\u4E4B\u5E74\u771F\u7684\u5230\u6765\uFF0C\u603B\u5F52\u8FD8\u662F\u4F1A\u6709\u4E9B\u6050\u614C\u548C\u4E0D\u5B89<br><br></p><p>2021\u5E74\u5BF9\u6211\u6765\u8BF4\u662F\u975E\u5E38\u5173\u952E\u7684\u4E00\u5E74</p><p>\u4ECA\u5E74\u4E5F\u662F\u505A\u4E86\u5F88\u591A\u5BF9\u6211\u800C\u8A00\u975E\u5E38\u91CD\u5927\u7684\u51B3\u5B9A</p><p>\u4E5F\u66FE\u575A\u5B9A\u3001\u51B3\u7136\uFF0C\u4E5F\u66FE\u72B9\u8C6B\u3001\u5F77\u5FA8</p><p>\u4F46\u662F\u4E5F\u76F8\u4FE1\u4E00\u5207\u90FD\u662F\u6700\u597D\u7684\u5B89\u6392</p><p><strong>\u811A\u8E0F\u5B9E\u5730\u3001\u83AB\u6025\u83AB\u8E81\uFF0C\u4F60\u60F3\u8981\u7684\u5C81\u6708\u603B\u4F1A\u7ED9\u4F60</strong></p><p>\u4E00\u8DEF\u5411\u524D\uFF0C\u76F8\u4FE1\u7ED3\u679C\u603B\u4E0D\u4F1A\u592A\u5DEE</p><h2 id="\u751F\u6D3B" tabindex="-1"><a class="header-anchor" href="#\u751F\u6D3B" aria-hidden="true">#</a> \u751F\u6D3B</h2><p><br><br><img src="https://img-blog.csdnimg.cn/img_convert/0da9b5685548d0fe4427cb91e7b1ca32.png" alt="\u56FE\u7247" loading="lazy"></p><p>2015\u5E743\u6708\uFF0C\u6211\u72EC\u81EA\u4E00\u4EBA\u6765\u5230\u5317\u4EAC\u53C2\u52A0\u590D\u8BD5</p><p>\u90A3\u4E5F\u662F\u6211\u7B2C\u4E00\u6B21\u4E00\u4E2A\u4EBA\u79BB\u5BB6\u90A3\u4E48\u8FDC</p><p>\u5F53\u65F6\u590D\u8BD5\u60C5\u51B5\u5E76\u4E0D\u7406\u60F3\uFF0C\u611F\u89C9\u53EF\u80FD\u4F1A\u8003\u7814\u5931\u8D25</p><p>\u90A3\u4E48\u8FD9\u5C06\u5F88\u53EF\u80FD\u662F\u7B2C\u4E00\u6B21</p><p>\u4E5F\u662F\u6211\u6700\u540E\u4E00\u6B21\u6765\u5317\u4EAC\u4E86</p><p>\u6240\u4EE5\u5728\u8FD4\u7A0B\u7684\u90A3\u5929\u65E9\u4E0A</p><p>\u6211\u7279\u610F\u53C8\u53BB\u4E86\u8D9F\u5B66\u6821</p><p>\u8FCE\u7740\u521D\u5347\u7684\u592A\u9633\uFF0C\u5728\u6821\u95E8\u53E3\u62CD\u4E0B\u4E86\u8FD9\u5F20\u7167\u7247<br><br></p><p>\u622A\u81F3\u4ECA\u5E74\uFF0C\u5DF2\u7ECF\u662F\u6211\u8FDC\u79BB\u5BB6\u4E61\u3001\u6765\u4EAC\u6F02\u6CCA\u7684\u7B2C\u4E03\u4E2A\u5E74\u5934\u4E86</p><p>\u4E5F\u662F\u5728\u8FD9\u7B2C\u4E03\u4E2A\u5E74\u5934</p><p>\u7ED3\u675F\u4E86\u8717\u5C45\u4ED6\u4EBA\u5C4B\u6A90\u4E0B\u7684\u751F\u6D3B\uFF0C<strong>\u7B97\u662F\u5B9A\u5C45\u5317\u4EAC</strong>\u4E86</p><p>2018\u5E74\u6BD5\u4E1A\u7684\u65F6\u5019\u6211\u5BF9\u81EA\u5DF1\u5B9A\u8FC7\u4E00\u4E2A\u76EE\u6807</p><p>\u5C31\u662F\u5B8C\u6210<strong>\u843D\u6237\u5317\u4EAC</strong>\u3001<strong>\u5B9A\u5C45\u5317\u4EAC</strong>\u3001<strong>\u6447\u4E2D\u5317\u4EAC\u8F66\u724C</strong>\u8FD9\u4E09\u4EF6\u4E8B</p><p>\u4ECA\u5E74\u5B8C\u6210\u4E86\u7B2C\u4E8C\u4EF6</p><p>\u8FD9\u4E5F\u7B97\u662F\u8FD1\u51E0\u5E74\u529E\u6210\u7684\u4E00\u4EF6\u5927\u4E8B\u4E86<br><br></p><p>\u751F\u6D3B\u4E0A\u4ECA\u5E74\u6700\u5927\u7684\u6539\u53D8\u662F\u9010\u6E10\u6709\u4E86\u5BB6\u7684\u611F\u89C9</p><p>\u6211\u4E00\u76F4\u90FD\u4E0D\u662F\u90A3\u79CD\u7279\u522B\u9ECF\u5BB6\u7684\u4EBA</p><p>\u4ECE\u9AD8\u4E2D\u6BD5\u4E1A\u4E0A\u5927\u5B66\u5F00\u59CB</p><p>\u4E00\u5E74\u57FA\u672C\u6700\u591A\u56DE\u5BB6\u4E24\u6B21\uFF0C\u6709\u65F6\u662F\u4E00\u6B21</p><p>\u4F46\u6BCF\u5468\u65E5\u665A\u7ED9\u5BB6\u91CC\u6253\u7535\u8BDD\u7684\u4E60\u60EF</p><p>\u4ECE\u521D\u4E2D\u5F00\u59CB\u4E00\u76F4\u4FDD\u7559\u5230\u4E86\u73B0\u5728</p><p>\u8BB0\u5F97\u4E2D\u5B66\u65F6\u671F\u4E0D\u8BA9\u5E26\u624B\u673A</p><p>\u6253\u7535\u8BDD\u7528\u7684\u662F201\u5361\u6253\u516C\u7528\u7535\u8BDD</p><p>\u6240\u4EE5\u6211\u9664\u4E86\u81EA\u5DF1\u7684\u7535\u8BDD</p><p>\u8BB0\u5F97\u6700\u719F\u7684\u5C31\u662F\u8001\u7238\u8001\u5988\u7684\u7535\u8BDD\u53F7\u7801\u4E86</p><p>\u73B0\u5728\u5728\u5317\u4EAC\u5EFA\u7ACB\u4E86\u81EA\u5DF1\u76842-3\u4EBA\u5C0F\u5BB6\u4E4B\u540E</p><p>\u6162\u6162\u5730\u5BF9\u201C\u5BB6\u201D\u4E5F\u6709\u4E86\u4E00\u4E9B\u521D\u6B65\u7684\u611F\u89C9</p><p>\u6211\u89C9\u5F97<strong>\u5BB6\u5176\u5B9E\u4E0D\u662F\u201C\u6E2F\u6E7E\u201D\uFF0C\u800C\u5E94\u8BE5\u662F\u201C\u7275\u6302\u201D</strong></p><p>\u65E0\u8BBA\u51FA\u884C\u8FDC\u8FD1\uFF0C\u603B\u6709\u4E00\u4EFD\u7275\u6302\u8BA9\u4F60\u56DE\u53BB\u7684\u5730\u65B9\uFF0C\u5C31\u662F\u5BB6</p><p>\u4E3A\u4E86\u8FD9\u4EFD\u7275\u6302</p><p>\u4F60\u4F1A\u505A\u66F4\u614E\u91CD\u7684\u51B3\u5B9A</p><p>\u505A\u66F4\u7406\u667A\u7684\u9009\u62E9</p><p>\u505A\u66F4\u5168\u9762\u7684\u8003\u91CF<br><br></p><p>\u4E5F\u7B97\u662F\u4EBA\u5230\u4E2D\u5E74\u5427\uFF0C\u751F\u6D3B\u4E2D\u53E6\u4E00\u4E2A\u53D8\u5316\u662F\u771F\u7684\u5F00\u59CB\u6CE8\u610F\u517B\u751F\u4E86</p><p>\u559D\u6C34\u4E5F\u5F00\u59CB\u559D\u70ED\u6C34\u4E86\uFF0C\u6C34\u676F\u91CC\u4E5F\u603B\u60F3\u6CE1\u70B9\u4E1C\u897F\u4E86</p><p>\u516C\u53F8\u65E9\u4E0A10\u70B9\u7EC4\u7EC7\u7684\u7B2C\u4E5D\u5957\u5E7F\u64AD\u4F53\u64CD\u4E5F\u662F\u975E\u5E38\u79EF\u6781\u5730\u53C2\u52A0\u4E86</p><p>\u4E2D\u5348\u5403\u5B8C\u996D\u4E5F\u517B\u6210\u4E86\u8E22\u8E22\u6BFD\u5B50\u7684\u4E60\u60EF\u4E86</p><p>\u4E0D\u7740\u6025\u7684\u65F6\u5019\u4E5F\u66F4\u613F\u610F\u653E\u4E0B\u5171\u4EAB\u5355\u8F66\u8D70\u8DEF\u6E9C\u8FBE\u4E86</p><p>\u79CB\u88E4\u4E5F\u7A7F\u7684\u6BD4\u5F80\u5E74\u65E9\u4E86\u8BB8\u591A\u4E86<br><br></p><p>\u8EAB\u4F53\u5065\u5EB7\u72B6\u51B5\u611F\u89C9\u4ECD\u7136\u826F\u597D</p><p>\u8FD9\u4E5F\u5F97\u76CA\u4E8E\u517B\u751F\u7684\u751F\u6D3B\u65B9\u5F0F\u548C\u5B9A\u671F\u7684\u7BEE\u7403\u3001\u7FBD\u6BDB\u7403\u6D3B\u52A8</p><p>\u4F53\u91CD\u7ECF\u8FC7\u4ECA\u5E74\u7684\u5C0F\u843D\u5C0F\u8D77</p><p>\u57FA\u672C\u53C8\u56DE\u5230\u4E86\u53BB\u5E74\u540C\u671F\u7684\u6C34\u5E73</p><p>\u5236\u5B9A\u7684\u51CF\u80A5\u8BA1\u5212\u968F\u77402021\u5E74\u6700\u540E\u4E00\u5929\u7684\u5230\u6765</p><p>\u6700\u7EC8\u5BA3\u544A\u5931\u8D25\u4E86</p><p>\u770B\u6765\u660E\u5E74\u8FD8\u5F97\u7EE7\u7EED<br><br></p><h2 id="\u5DE5\u4F5C" tabindex="-1"><a class="header-anchor" href="#\u5DE5\u4F5C" aria-hidden="true">#</a> \u5DE5\u4F5C</h2><blockquote><p>\u6E29\u9152\u5165\u5589\u522B\u4E09\u79CB\uFF0C\u7EB8\u77ED\u60C5\u957F\u705E\u6865\u67F3\uFF1B\u4ED6\u65E5\u91CD\u805A\u9996\uFF0C\u4ECD\u613F\u8863\u65B0\u4EBA\u65E7\u3002</p><p>\u5728\u4E03\u5BA4\u6211\u5EA6\u8FC7\u4E86\u975E\u5E38\u96BE\u5FD8\u7684\u4E09\u5E74\uFF0C\u6765\u4E0D\u53CA\u4E0E\u5404\u4F4D\u540C\u4E8B\u4E00\u4E00\u544A\u522B\uFF0C\u8FD8\u671B\u89C1\u8C05\u3002\u5C0F\u6234\u771F\u5FC3\u611F\u8C22\u4E09\u5E74\u6765\u5404\u4F4D\u9886\u5BFC\u3001\u540C\u4E8B\u5BF9\u6211\u7684\u5E2E\u52A9\u548C\u652F\u6301\uFF0C\u6211\u4F1A\u94ED\u8BB0\u4E8E\u5FC3\u3002\u805A\u662F\u706B\uFF0C\u6563\u662F\u661F\uFF0C\u5E0C\u671B\u4E03\u5BA4\u8D8A\u6765\u8D8A\u597D\uFF0C\u5927\u5BB6\u8D8A\u6765\u8D8A\u597D\uFF0C\u6C5F\u6E56\u4E0D\u8FDC\uFF0C\u6211\u4EEC\u6709\u7F18\u518D\u89C1\u3002</p></blockquote><p><br><br> 2018\u5E74\u6BD5\u4E1A\u5165\u804C\u6765\u5230\u4E86\u4E91\u5C97\u8FD9\u4E2A\u5730\u65B9</p><p>\u81F3\u4ECA\u4E5F\u4E09\u5E74\u6709\u4F59\u4E86</p><p>\u4ECA\u5E74\u505A\u7684\u53E6\u5916\u4E00\u4EF6\u5927\u51B3\u5B9A\u5C31\u662F<strong>\u8F9E\u6389\u4E86\u6211\u6BD5\u4E1A\u540E\u7684\u7B2C\u4E00\u4EFD\u5DE5\u4F5C</strong></p><p>\u4E0A\u9762\u8FD9\u6BB5\u8BDD\u5C31\u662F\u6211\u79BB\u804C\u540E\u56DE\u5BB6\u7684\u9AD8\u94C1\u4E0A\u7F16\u8F91\u7684</p><p>\u53D1\u9001\u5230\u4E86\u90E8\u95E8\u7FA4\u540E\uFF0C\u5C31\u9000\u51FA\u7FA4\u804A\u4E86</p><p>\u4E5F\u7B97\u662F\u7ED9\u6211\u8FC7\u53BB\u7684\u8FD9\u6BB5\u65F6\u95F4\u753B\u4E0A\u4E00\u4E2A\u53E5\u53F7<br><br></p><p>\u5173\u4E8E\u5DE5\u4F5C\u4ECA\u5E74\u4E5F\u662F\u6BD4\u8F83\u574E\u5777</p><p>\u90FD\u8BF4\u4ECA\u5E74\u662F<strong>\u4E92\u8054\u7F51\u884C\u4E1A\u7684\u5BD2\u51AC</strong></p><p>\u90A3\u6211\u662F\u771F\u771F\u5B9E\u5B9E\u7ECF\u5386\u4E86<strong>\u4E00\u573A\u51B0\u96F9</strong></p><p>\u4ECE\u4E00\u4E2A\u7B97\u662F\u94C1\u996D\u7897\u7684\u4E8B\u4E1A\u5355\u4F4D\u79BB\u804C\u540E\u5165\u804C\u65B0\u516C\u53F8\u4E0D\u5230\u4E00\u4E2A\u6708\u7684\u65F6\u95F4</p><p>\u5C31\u7ECF\u5386\u4E86\u53C8\u4E00\u6B21\u5931\u4E1A</p><p>\u804C\u573A\u7684\u6B8B\u9177\u4E5F\u7740\u5B9E\u628A\u6211\u4E4B\u524D\u89C4\u5212\u597D\u7684\u5404\u79CD\u8BA1\u5212\u6495\u5F97\u7C89\u788E</p><p>\u6240\u4EE5\u518D\u4E00\u6B21\u9009\u62E9\u5DE5\u4F5C\u5355\u4F4D\u7684\u65F6\u5019</p><p>\u7ECF\u8FC7\u518D\u4E09\u6743\u8861\u548C\u601D\u8003</p><p>\u8FD8\u662F\u9009\u62E9\u4E86\u79BB\u5BB6\u6700\u8FD1\u3001\u72B6\u6001\u6700\u7A33\u5B9A\u7684\u4E00\u4E2A</p><p>\u867D\u7136\u53EF\u80FD\u4F1A\u5C11\u4E86\u5F88\u591A\u53EF\u80FD\u6027</p><p>\u4F46\u662F\u5F88\u591A\u51B3\u5B9A\u786E\u5B9E\u4E0D\u80FD\u53EA\u8003\u8651\u5230\u81EA\u5DF1\u4E86<br><br></p><p>\u4ECA\u5E74\u7684\u5DE5\u4F5C\u4E0D\u80FD\u7B97\u975E\u5E38\u987A\u5229</p><p>\u4F46\u603B\u4F53\u8FD8\u662F\u5E73\u7A33\u8FC7\u6E21\u4E86</p><p>\u4E5F\u662F\u81EA\u53C2\u52A0\u5DE5\u4F5C\u4EE5\u6765\u6700\u8F7B\u677E\u5EA6\u8FC7\u7684\u4E00\u5E74\u4E86</p><p>\u5E74\u4E2D\u7684\u65F6\u5019\u4E5F\u662F\u975E\u5E38\u96BE\u5F97\u5730\u6709\u5C06\u8FD12\u4E2A\u6708\u7684\u65F6\u95F4\u91CD\u65B0\u89C4\u5212\u81EA\u5DF1\u672A\u6765\u7684\u65B9\u5411</p><p>\u4E0D\u8FC7\u8FD9\u79CD\u592A\u8FC7\u8D85\u4E4E\u8BA1\u5212\u4E4B\u5185\u7684\u5DE5\u4F5C\u53D8\u52A8</p><p>\u6211\u662F\u518D\u4E0D\u60F3\u7ECF\u5386\u7B2C\u4E8C\u904D</p><h2 id="\u5B66\u4E60" tabindex="-1"><a class="header-anchor" href="#\u5B66\u4E60" aria-hidden="true">#</a> \u5B66\u4E60</h2><p><br><br> \u8FD9\u662F\u6211\u89C9\u5F97<strong>\u4ECA\u5E74\u505A\u7684\u505A\u5DEE\u7684\u4E00\u4E2A\u65B9\u9762</strong><br><br></p><p>\u4E0A\u534A\u5E74\u6709\u4E9B\u6D51\u6D51\u5669\u5669\uFF0C\u4E00\u5FC3\u60F3\u7740\u8F9E\u804C</p><p>\u5E74\u4E2D\u53C8\u7ECF\u5386\u4E00\u573A\u5931\u4E1A\u7684\u98CE\u6CE2</p><p>\u4E0B\u534A\u5E74\u624D\u6162\u6162\u878D\u5165\u65B0\u7684\u5DE5\u4F5C\u548C\u751F\u6D3B\u73AF\u5883</p><p>\u4ECA\u5E74\u5236\u5B9A\u7684\u5B66\u4E60\u8BA1\u5212\u6267\u884C\u60C5\u51B5\u5C31\u662F\u4E0D\u53CA\u683C<br><br></p><p>\u672C\u6765\u8FD8\u60F3\u53C2\u52A0\u8F6F\u8003\u53D6\u5F97\u4E2A\u8BC1\u4E66</p><p>\u4E00\u65B9\u9762\u81EA\u5DF1\u4E5F\u6CA1\u6709\u8BA4\u771F\u51C6\u5907</p><p>\u53E6\u4E00\u65B9\u9762\u56E0\u4E3A\u75AB\u60C5\uFF0C\u5317\u4EAC\u76F4\u63A5\u53D6\u6D88\u4E86\u4ECA\u5E74\u7684\u8003\u8BD5<br><br></p><p>\u9006\u6C34\u884C\u821F\uFF0C\u4E0D\u8FDB\u5219\u9000</p><p>\u4FDD\u6301\u5B66\u4E60\u3001\u4FDD\u6301\u4E13\u4E1A</p><p>\u8FD9\u4E5F\u662F<strong>\u4FDD\u8BC1\u81EA\u5DF1\u5177\u6709\u7ADE\u4E89\u529B</strong>\u6700\u6839\u672C\u7684\u8981\u6C42</p><p>\u4ECA\u5E74\u6B20\u4E0B\u7684\uFF0C\u660E\u5E74\u4E00\u5B9A\u8981\u8865\u56DE\u6765\u4E86<br><br></p><h2 id="\u516C\u4F17\u53F7" tabindex="-1"><a class="header-anchor" href="#\u516C\u4F17\u53F7" aria-hidden="true">#</a> \u516C\u4F17\u53F7</h2><blockquote><p>\u4E34\u8FD1\u800C\u7ACB\u4E4B\u5E74\uFF0C\u5DE5\u4F5C\u4E8E\u4F53\u5236\u5185\u5DF2\u8FC7\u4E09\u8F7D\uFF0C\u81EA\u89C9\u65E0\u6709\u6240\u6210\uFF0C\u53C8\u5ACC\u4E0D\u601D\u8FDB\u53D6\uFF0C\u6545\u6709\u7ECF\u8425\u516C\u4F17\u53F7\u4E4B\u601D\uFF0C\u540D\u66F0\u201C\u4F60\u597D\u6234\u5148\u751F\u201D\uFF0C\u4E0D\u5B9A\u671F\u53D1\u5E03\u6280\u672F\u53CA\u53CD\u601D\u4E4B\u6587\uFF0C\u6B32\u4E0E\u543E\u8F88\u540C\u4FEE\uFF0C\u8FD8\u671B\u4F17\u4EBA\u9F0E\u529B\u5173\u6CE8\u652F\u6301\uFF0C\u6234\u67D0\u4EBA\u4E0D\u80DC\u611F\u6FC0\u3002</p></blockquote><p><br><br> \u8FD9\u662F\u6211\u516C\u4F17\u53F7\u201C\u4F60\u597D\u6234\u5148\u751F\u201D\u7684\u7B80\u4ECB</p><p>\u6700\u521D\u60F3\u505A\u8FD9\u4E2A\u516C\u4F17\u53F7\u6709\u4E24\u4E2A\u60F3\u6CD5<br><br></p><p>\u5176\u4E00\u662F\u60F3<strong>\u5BF9\u81EA\u5DF1\u7684\u4E13\u4E1A\u6709\u4E2A\u4EA4\u4EE3</strong></p><p>\u56E0\u4E3A\u8EAB\u5904\u76F8\u5BF9\u7A33\u5B9A\u3001\u6280\u672F\u504F\u8001\u7684\u592E\u4F01\u4E8B\u4E1A\u5355\u4F4D</p><p>\u968F\u7740\u5BF9\u73AF\u5883\u7684\u719F\u6089\u548C\u9002\u5E94\uFF0C\u81EA\u89C9\u5DE5\u4F5C\u662F\u66F4\u52A0\u9A7E\u8F7B\u5C31\u719F</p><p>\u4F46\u662F\u6280\u672F\u7684\u6210\u957F\u5374\u505C\u6EDE\u4E0D\u524D</p><p>\u6DF1\u77E5\u5F53\u5E74\u4ECE\u4E00\u6240\u6CB3\u5357\u57AB\u5E95\u7684\u666E\u901A\u4E8C\u672C</p><p>\u8003\u5165\u5317\u79D1\u5927\u7684\u8BA1\u7B97\u673A\u7855\u58EB\u7814\u7A76\u751F\u7684\u4E0D\u6613</p><p>\u7EDD\u4E0D\u80FD\u8BA9\u81EA\u5DF1\u8FD9\u4E48\u8F7B\u6613\u4E22\u6389\u81EA\u5DF1\u7684\u4E13\u4E1A</p><p>\u6240\u4EE5\u60F3\u901A\u8FC7\u8FD9\u79CD\u65B9\u5F0F\u7763\u4FC3\u81EA\u5DF1\u6301\u7EED\u5B66\u4E60<br><br></p><p>\u5176\u4E8C\u662F<strong>\u5206\u4EAB\u548C\u8868\u8FBE\u81EA\u5DF1\u7684\u4E00\u4E9B\u60F3\u6CD5\u548C\u6001\u5EA6</strong></p><p>\u6211\u5F88\u591A\u65F6\u5019\u90FD\u89C9\u5F97\u5982\u679C\u6211\u6CA1\u6709\u505A\u7A0B\u5E8F\u5458</p><p>\u53EF\u80FD\u66F4\u9002\u5408\u6211\u7684\u5DE5\u4F5C\u662F\u4E00\u540D\u8001\u5E08</p><p>\u6211\u975E\u5E38\u4EAB\u53D7\u628A\u81EA\u5DF1\u7684\u7ECF\u9A8C\u3001\u60F3\u6CD5\u3001\u6001\u5EA6\u3001\u77E5\u8BC6\u5206\u4EAB\u51FA\u53BB\u7684\u611F\u89C9</p><p>\u613F\u610F\u4E0E\u5FD7\u540C\u9053\u5408\u4E4B\u4EBA\u5171\u540C\u5B66\u4E60\u3001\u4EA4\u6D41<br><br></p><p>\u7B97\u4E0A\u8FD9\u7BC7\u6587\u7AE0\uFF0C2021\u5E74\u5171\u53D1\u4E8625\u7BC7\u6587\u7AE0</p><p>\u5176\u4E2D\u6280\u672F\u7C7B15\u7BC7\uFF0C\u5176\u4ED6\u7C7B10\u7BC7</p><p>\u76F8\u8F83\u5E74\u521D\uFF0C\u5173\u6CE8\u4EBA\u6570\u589E\u52A0\u4E8674.4%</p><p>\u5E73\u5747\u4E0B\u6765\u4E00\u4E2A\u6708\u4E5F\u5C31\u4E24\u7BC7\u6587\u7AE0\uFF0C\u6280\u672F\u7C7B\u7684\u4E5F\u5C31\u4E00\u7BC7</p><p>\u6BD4\u6211\u9884\u671F\u7684\u8FD8\u662F\u5C11\u4E86\u5F88\u591A</p><p>\u4E3B\u8981\u539F\u56E0\u5176\u5B9E\u8FD8\u662F\u6267\u884C\u529B\u4E0D\u591F</p><p>\u5F88\u5BB9\u6613\u88AB\u4E00\u4E9B\u4E8B\u60C5\u6253\u65AD</p><p>\u800C\u4E14\u4E00\u6253\u65AD\u5C31\u4F1A\u65AD\u66F4\u5F88\u957F\u65F6\u95F4</p><p>\u6240\u4EE5\u516C\u4F17\u53F7\u8FD9\u65B9\u97622021\u5E74\u6211\u53EA\u80FD\u7ED9\u81EA\u5DF1\u6253\u4E00\u4E2A\u53CA\u683C\u5206</p><h2 id="\u5C0F\u76EE\u6807" tabindex="-1"><a class="header-anchor" href="#\u5C0F\u76EE\u6807" aria-hidden="true">#</a> \u5C0F\u76EE\u6807</h2><blockquote><p>\u77E5\u884C\u5408\u4E00\uFF0C\u8FD9\u5E94\u8BE5\u5C31\u662F\u6211\u5BF9\u81EA\u5DF12022\u5E74\u6700\u5927\u7684\u8981\u6C42\u5427</p></blockquote><p><br><br> 2022\u5E74\uFF0C\u7B97\u662F\u6211\u4EBA\u751F\u53E6\u4E00\u4E2A\u9636\u6BB5\u7684\u5F00\u59CB</p><p>\u8270\u96BE\u65B9\u663E\u52C7\u6BC5\uFF0C\u78E8\u783A\u59CB\u5F97\u7389\u6210</p><p>\u65B0\u7684\u4E00\u5E74\u4E0D\u60F3\u7ED9\u81EA\u5DF1\u5B9A\u592A\u8FC7\u5177\u4F53\u7684\u76EE\u6807</p><p>\u53EA\u60F3\u8BA9\u81EA\u5DF1\u771F\u6B63\u505A\u5230\u77E5\u884C\u5408\u4E00</p><p>\u628A<strong>\u8981\u505A\u7684\u4E8B\u60C5\u505A\u5B8C</strong></p><p>\u628A<strong>\u60F3\u505A\u7684\u4E8B\u60C5\u505A\u597D</strong></p><p>\u628A<strong>\u80FD\u505A\u7684\u4E8B\u60C5\u505A\u5230</strong></p><p>\u4E0D\u6127\u4E8E\u5FC3\uFF0C\u4E0D\u6094\u4E8E\u5DF1\uFF0C\u4E0D\u61BE\u4E8E\u4EBA <br><br></p><p>\u52AA\u529B\u8BA9\u516C\u4F17\u53F7\u5173\u6CE8\u4EBA\u6570\u7FFB\u4E00\u500D</p><p>\u52AA\u529B\u53D1\u8868\u516C\u4F17\u53F7\u6587\u7AE040\u7BC7</p><p>\u52AA\u529B\u5B8C\u6210\u4E09\u672C\u4E13\u4E1A\u6280\u672F\u4E66\u7C4D\u7684\u9605\u8BFB</p><p>\u52AA\u529B\u62FF\u5230\u8F6F\u8003\u7684\u8BC1\u4E66</p><p>\u52AA\u529B\u51CF\u91CD5\u516C\u65A4</p><p>\u52AA\u529B\u5B66\u4E00\u9879\u65B0\u6280\u80FD</p><p>\u52AA\u529B\u5DE5\u4F5C\u4E0A\u518D\u4E0A\u4E00\u4E2A\u53F0\u9636 <br><br></p><p>\u4ECA\u5E74\u7684\u4E2A\u4EBA\u603B\u7ED3\u81F3\u6B64\u5B8C\u7ED3</p><p>\u613F\u6211\u4EEC\u6BCF\u4E00\u4E2A\u4EBA\u8D8A\u6765\u8D8A\u597D\uFF0C\u6240\u60F3\u80FD\u6210<br><br></p><p>\u660E\u5E74\u518D\u89C1<br><br></p><div style="text-align:right;"><p>\u6587/\u6234\u5148\u751F@2021\u5E7412\u670830\u65E5</p></div>',144),b=[e];function s(a,g){return r(),t("div",null,b)}var h=p(n,[["render",s],["__file","sanding_in_front_of_the_30-year-old_door.html.vue"]]);export{h as default};
