import{_ as a}from"./plugin-vue_export-helper.21dcd24c.js";import{o as e,c as r,e as p}from"./app.6abff923.js";const h={},i=p('<p>\u7B14\u8005\u81EA\u6BD5\u4E1A\u540E\u4E00\u76F4\u5C31\u804C\u4E8E\u67D0\u4E8B\u4E1A\u7F16\u7684\u592E\u4F01\uFF0C\u4ECA\u5E74\u4E94\u6708\u4EFD\u79BB\u804C\u540E\u9009\u62E9\u53BB\u4E86\u4E2D\u56FD\u5E73\u5B89\u67D0\u9879\u76EE\u56E2\u961F</p><p>\u5948\u4F55\u9020\u5316\u5F04\u4EBA\uFF0C\u5F53\u4E86\u70AE\u7070\uFF0C\u5165\u804C\u4EC5\u4EC5\u4E24\u5468\u5C31\u88AB\u544A\u77E5\u9879\u76EE\u8C03\u6574\uFF0C\u6574\u4E2A\u51E0\u767E\u4EBA\u7684\u9879\u76EE\u56E2\u961F\u90FD\u8981\u88AB\u4F18\u5316</p><p>\u4ECE\u653E\u5F03\u4E00\u4E2A\u62E5\u6709\u94C1\u996D\u7897\u7684\u5355\u4F4D\uFF0C\u5230\u518D\u6B21\u5931\u4E1A\u4EC5\u4EC5\u4E5F\u5C31\u4E24\u5468\u7684\u65F6\u95F4\uFF0C\u8BA9\u6211\u4E5F\u5BF9\u81EA\u5DF1\u7684\u9009\u62E9\u90FD\u4EA7\u751F\u4E86\u6000\u7591</p><p>\u5F53\u7136\uFF0C\u5199\u8FD9\u7BC7\u6587\u7AE0\u4E0D\u662F\u4E3A\u4E86\u9ED1\u4E2D\u56FD\u5E73\u5B89\uFF0C\u5BF9\u4E8E\u8BD5\u7528\u671F\u88AB\u88C1\uFF0C\u5E73\u5B89\u4E5F\u7ED9\u51FA\u4E86\u201CN+2\u201D+ \u4E00\u6708\u591A\u6708\u5DE5\u8D44 + \u4E24\u4E2A\u6708\u516C\u79EF\u91D1\u793E\u4FDD\u7684\u65B9\u6848\uFF0C\u8FD8\u7B97\u662F\u826F\u5FC3\u4E86</p><p>\u751F\u6D3B\u8FD8\u8981\u7EE7\u7EED\uFF0C\u4E4B\u540E\u5C31\u5F00\u542F\u4E86\u4E3A\u671F\u8FD1\u4E00\u4E2A\u6708\u7684\u9762\u8BD5\u4E4B\u65C5\uFF0C\u5176\u4E2D\u6709\u6700\u7EC8\u8FDB\u5165\u5230\u8C08\u85AA\u548Coffer\u7684\u73AF\u8282</p><p>\u56E0\u4E3A\u6BD5\u4E1A\u7684\u65F6\u5019\u5C31\u9762\u4E86\u4E00\u5BB6\u5355\u4F4D\u5C31\u76F4\u63A5\u5165\u804C\u4E86\uFF0C\u6240\u4EE5\u8FD9\u6B21\u8DDF\u5404\u4E92\u8054\u7F51\u516C\u53F8\u7684\u9762\u8BD5\u8FD8\u662F\u8BA9\u6211\u5B66\u5230\u4E86\u4E0D\u5C11\u5957\u8DEF</p><p>\u5176\u4E2D\u5C31\u6709\u5173\u4E8E\u4F01\u4E1A\u4E94\u9669\u4E00\u91D1\u7684\u7F34\u7EB3\uFF0C\u4E0B\u9762\u6211\u8C08\u8C08\u6211\u7684\u4E00\u4E9B\u60F3\u6CD5\u548C\u611F\u53D7\uFF0C\u4E5F\u5E0C\u671B\u5404\u4F4D\u65B0\u4E00\u4EE3\u519C\u6C11\u5DE5\u80FD\u591F\u6709\u6240\u83B7\u76CA <br><br></p><h2 id="\u4EC0\u4E48\u662F\u4E94\u9669\u4E00\u91D1" tabindex="-1"><a class="header-anchor" href="#\u4EC0\u4E48\u662F\u4E94\u9669\u4E00\u91D1" aria-hidden="true">#</a> \u4EC0\u4E48\u662F\u4E94\u9669\u4E00\u91D1</h2><p>\u4E94\u9669\u4E00\u91D1\u662F\u6307\u517B\u8001\u4FDD\u9669\u3001\u533B\u7597\u4FDD\u9669\u3001\u5DE5\u4F24\u9669\u3001\u5931\u4E1A\u9669\u3001\u751F\u80B2\u4FDD\u9669\u4EE5\u53CA\u516C\u79EF\u91D1\uFF0C\u6709\u4E00\u4E9B\u4F01\u4E1A\u4F1A\u5728\u6B64\u57FA\u7840\u4E0A\u591A\u7F34\u7EB3\u5546\u4E1A\u4FDD\u9669\u548C\u4F01\u4E1A\u5E74\u91D1\uFF0C\u6784\u6210\u201C\u516D\u9669\u4E24\u91D1\u201D</p><p>\u5F88\u591A\u4EBA\u53EA\u77E5\u9053\u5DE5\u8D44\u8D8A\u9AD8\uFF0C\u8981\u7F34\u7EB3\u7684\u4E94\u9669\u4E00\u91D1\u5C31\u8D8A\u591A\uFF0C\u53D1\u5DE5\u8D44\u7684\u65F6\u5019\u6263\u7684\u94B1\u4E5F\u5C31\u8D8A\u591A\uFF0C\u6240\u4EE5\u5F88\u591A\u4EBA\u4F1A\u6709\u4E00\u4E9B\u4E0D\u7406\u89E3\u7684\u60C5\u7EEA</p><p>\u5176\u5B9E\u8FD9\u662F\u4E00\u9879\u7ED9\u6253\u5DE5\u4EBA\u7684\u793E\u4F1A\u798F\u5229\uFF0C\u662F\u56FD\u5BB6\u8981\u6C42\u4F01\u4E1A\u627F\u62C5\u7684\u4E00\u79CD\u793E\u4F1A\u8D23\u4EFB \u5F53\u7136\uFF0C\u6BD5\u7ADF\u6700\u7EC8\u53D7\u76CA\u4EBA\u662F\u81EA\u5DF1\uFF0C\u6240\u4EE5\u6253\u5DE5\u4EBA\u672C\u8EAB\u4E5F\u662F\u8981\u627F\u62C5\u5176\u4E2D\u4E00\u5C0F\u90E8\u5206\u8D23\u4EFB\uFF0C\u8FD9\u5C31\u662F\u5DE5\u8D44\u6761\u4E2D\u6263\u9664\u7684\u90A3\u4E00\u90E8\u5206\u94B1</p><h2 id="\u4E94\u9669\u4E00\u91D1\u6709\u4EC0\u4E48\u7528" tabindex="-1"><a class="header-anchor" href="#\u4E94\u9669\u4E00\u91D1\u6709\u4EC0\u4E48\u7528" aria-hidden="true">#</a> \u4E94\u9669\u4E00\u91D1\u6709\u4EC0\u4E48\u7528</h2><h3 id="\u517B\u8001\u4FDD\u9669" tabindex="-1"><a class="header-anchor" href="#\u517B\u8001\u4FDD\u9669" aria-hidden="true">#</a> \u517B\u8001\u4FDD\u9669</h3><p>\u987E\u540D\u601D\u4E49\uFF0C\u7528\u6765\u517B\u8001\u7684\u3002\u5C31\u662F\u9000\u4F11\u4E4B\u540E\uFF0C\u6309\u7167\u6708\u8FDB\u884C\u517B\u8001\u91D1\u53D1\u653E\u3002\u4E00\u822C\u8981\u4EA4\u6EE115\u5E74\uFF0C\u5230\u9000\u4F11\u7684\u65F6\u5019\u624D\u80FD\u7EC8\u751F\u4EAB\u53D7\u517B\u8001\u91D1\uFF0C\u9000\u4F11\u540E\u5177\u4F53\u6BCF\u6708\u80FD\u9886\u591A\u5C11\uFF0C\u6709\u4E00\u4E2A\u975E\u5E38\u590D\u6742\u7684\u8BA1\u7B97\u516C\u5F0F\uFF0C\u4F46\u662F\u7B80\u5355\u6765\u8BF4\uFF0C\u5C31\u662F\u4EA4\u7684\u8D8A\u591A\u3001\u4EA4\u7684\u65F6\u95F4\u8D8A\u957F\uFF0C\u5C06\u6765\u517B\u8001\u91D1\u5C31\u8D8A\u591A\u3002\u5177\u4F53\u7F34\u7EB3\u591A\u5C11\uFF0C\u8DDF\u4F01\u4E1A\u7ED9\u804C\u5DE5\u6307\u5B9A\u7684\u7F34\u7EB3\u57FA\u6570\u6709\u5173\uFF0C\u7F34\u7EB3\u6BD4\u4F8B\u662F\uFF1A\u804C\u5DE5\u7F34\u7EB38%\uFF0C\u800C\u4F01\u4E1A\u8981\u5E2E\u4F60\u7F34\u7EB316%</p><h3 id="\u533B\u7597\u4FDD\u9669" tabindex="-1"><a class="header-anchor" href="#\u533B\u7597\u4FDD\u9669" aria-hidden="true">#</a> \u533B\u7597\u4FDD\u9669</h3><p>\u89C1\u540D\u77E5\u610F\uFF0C\u7528\u6765\u770B\u75C5\u7684\u3002\u5C31\u662F\u53BB\u533B\u9662\u770B\u75C5\u7684\u65F6\u5019\uFF0C\u53EF\u4EE5\u62A5\u9500\u4E00\u5B9A\u7684\u6BD4\u4F8B\u7684\u836F\u8D39\u548C\u533B\u7597\u8D39\uFF0C\u4ECE\u7F34\u7EB3\u7684\u90A3\u5929\u8D77\uFF0C\u5C31\u53EF\u4EE5\u4F7F\u7528\u4E86\u3002\u4EE5\u5317\u4EAC\u5E02\u4E3A\u4F8B\uFF1A\u518D\u8D77\u4ED8\u7EBF\u4EE5\u4E0A\uFF0C\u95E8\u8BCA\u62A5\u9500\u53EF\u8FBE70%+\u3001\u4F4F\u9662\u62A5\u950085%+\uFF0C\u5982\u679C\u4F01\u4E1A\u8FD8\u5E2E\u5FD9\u7F34\u7EB3\u4E86\u5546\u4E1A\u4FDD\u9669\uFF0C\u57FA\u672C\u770B\u75C5\u62A5\u9500\u6BD4\u4F8B\u80FD\u8FBE\u523090%+\u3002\u800C\u5173\u4E8E\u8FD9\u9879\u4FDD\u9669\uFF0C\u6253\u5DE5\u4EBA\u53EA\u9700\u7F34\u7EB32%+3\u5757\u94B1\uFF08\u8865\u5145\u533B\u7597\uFF09\uFF0C\u4F01\u4E1A\u9700\u8981\u7F34\u7EB39%</p><h3 id="\u516C\u79EF\u91D1" tabindex="-1"><a class="header-anchor" href="#\u516C\u79EF\u91D1" aria-hidden="true">#</a> \u516C\u79EF\u91D1</h3><p>\u8FD9\u4E2A\u76F8\u8F83\u5176\u4ED6\u4E09\u4E2A\u4FDD\u9669\u66F4\u52A0\u91CD\u8981\uFF0C\u6211\u5C31\u653E\u5230\u524D\u8FB9\u8BF4\u4E86\u3002\u516C\u79EF\u91D1\u662F\u5E2E\u52A9\u6253\u5DE5\u4EBA\u4E70\u623F\u3001\u79DF\u623F\u3001\u88C5\u4FEE\u7528\u7684\uFF0C\u4E0E\u5176\u4ED6\u793E\u4F1A\u4FDD\u9669\u4E0D\u540C\u7684\u662F\uFF0C\u8FD9\u9879\u798F\u5229\u662F\u5426\u7F34\u7EB3\u662F\u7531\u4F01\u4E1A\u51B3\u5B9A\u7684\uFF0C\u7F34\u7EB3\u57FA\u6570\u4F01\u4E1A\u5B9A\u3001\u7F34\u7EB3\u6BD4\u4F8B\u4E5F\u662F\u4F01\u4E1A\u5B9A\uFF085%-12%\uFF09\uFF0C\u4F46\u662F\u4E2A\u4EBA\u4E5F\u4F01\u4E1A\u7F34\u7EB3\u7684\u767E\u5206\u6BD4\u662F\u4E00\u6837\u7684\uFF0C\u4E5F\u5C31\u662F\u8BF4\u54B1\u4EEC\u6253\u5DE5\u4EBA\u7F34\u7EB3\u591A\u5C11\uFF0C\u4F01\u4E1A\u4E5F\u5FC5\u987B\u7F34\u7EB3\u591A\u5C11\uFF0C\u800C\u4E14\u4E24\u8005\u4EA4\u7684\u94B1\u90FD\u662F\u76F4\u63A5\u6253\u5230\u54B1\u4EEC\u6253\u5DE5\u4EBA\u7684\u4E2A\u4EBA\u8D26\u6237\u4E0A\u7684\uFF0C\u7B26\u5408\u4E00\u5B9A\u6761\u4EF6\u662F\u53EF\u4EE5\u76F4\u63A5\u652F\u53D6\u7684\uFF0C\u66F4\u91CD\u8981\u7684\u662F\uFF0C\u53EF\u4EE5\u4E70\u623F\u7684\u65F6\u5019\u7533\u8BF7\u4E00\u5B9A\u91D1\u989D\u7684\u4F4E\u606F\u8D37\u6B3E\uFF0C\u5DEE\u4E0D\u591A\u662F\u5546\u4E1A\u8D37\u6B3E\u5229\u606F\u7684\u4E00\u534A\uFF0C\u4EE5\u5317\u4EAC\u4E3A\u4F8B\uFF1A\u5E02\u7BA1\u516C\u79EF\u91D1\u9996\u6B21\u53EF\u4EE5\u6309\u7167\u7F34\u7EB3\u7684\u5E74\u9650*10\u4E07\u8FDB\u884C\u8D37\u6B3E\uFF0C\u5982\u679C\u662F\u56FD\u7BA1\u516C\u79EF\u91D1\u9996\u6B21\u53EF\u4EE5\u5B9A\u683C\u8D37120\u4E07\uFF01</p><h3 id="\u5931\u4E1A\u4FDD\u9669" tabindex="-1"><a class="header-anchor" href="#\u5931\u4E1A\u4FDD\u9669" aria-hidden="true">#</a> \u5931\u4E1A\u4FDD\u9669</h3><p>\u8981\u4EA4\u6EE1\u4E00\u5E74\u624D\u80FD\u4EAB\u53D7\uFF0C\u4E00\u822C\u4EA41\u5E74\u62FF2\u4E2A\u6708\uFF0C\u4EA42\u5E74\u62FF4\u4E2A\u6708\uFF0C\u4F46\u4E00\u8F88\u5B50\u6700\u591A\u62FF24\u4E2A\u6708\u3002\u4E2A\u4EBA\u548C\u5355\u4F4D\u7684\u7F34\u7EB3\u6BD4\u4F8B\u90FD\u662F0.5%\uFF0C\u518D\u5931\u4E1A\u7684\u60C5\u51B5\u4E0B\u53EF\u4EE5\u7533\u8BF7\u9886\u53D6\uFF0C\u4F46\u662F\u73B0\u5728\u7684\u793E\u4F1A\uFF0C\u9664\u975E\u5BF9\u5C97\u4F4D\u8981\u6C42\u592A\u9AD8\uFF0C\u4E00\u822C\u5F88\u5C11\u88AB\u5931\u4E1A\u540E\u4E00\u4E2A\u6708\u8FD8\u627E\u4E0D\u5230\u5DE5\u4F5C\u7684\u3002</p><h3 id="\u751F\u80B2\u4FDD\u9669" tabindex="-1"><a class="header-anchor" href="#\u751F\u80B2\u4FDD\u9669" aria-hidden="true">#</a> \u751F\u80B2\u4FDD\u9669</h3><p>\u53EF\u4EE5\u62A5\u9500\u751F\u5C0F\u5B69\u65F6\u5019\u7684\u4E00\u4E9B\u533B\u7597\u82B1\u9500\uFF0C\u4E2A\u4EBA\u4E0D\u7528\u7F34\u7EB3\uFF0C\u5355\u4F4D\u7F34\u7EB30.8%\uFF0C\u73B0\u5728\u8FD9\u9879\u5DF2\u7ECF\u5F52\u7ED3\u5230\u533B\u7597\u4FDD\u9669\u91CC\u8FB9\u4E86\uFF0C\u6240\u4EE5\u5176\u5B9E\u73B0\u5728\u533B\u7597\u4FDD\u9669\u4F01\u4E1A\u5E94\u7F34\u7EB39.8%</p><h3 id="\u5DE5\u4F24\u4FDD\u9669" tabindex="-1"><a class="header-anchor" href="#\u5DE5\u4F24\u4FDD\u9669" aria-hidden="true">#</a> \u5DE5\u4F24\u4FDD\u9669</h3><p>\u4F7F\u7528\u518D\u56E0\u516C\u53D7\u4F24\u7684\u60C5\u51B5\u8FDB\u884C\u8865\u507F\u7684\u4FDD\u9669\uFF0C\u5305\u62EC\u5DE5\u4F5C\u5F53\u4E2D\u4EE5\u53CA\u53BB\u5DE5\u4F5C\u7684\u8DEF\u4E0A\u53D1\u751F\u7684\u610F\u5916\u60C5\u51B5\u3002\u540C\u6837\u8FD9\u9879\u4FDD\u9669\u4E2A\u4EBA\u4E5F\u4E0D\u9700\u8981\u7F34\u7EB3\uFF0C\u4F01\u4E1A\u7F34\u7EB30.4%</p><h2 id="hr\u53E3\u4E2D\u7684\u907F\u7A0E" tabindex="-1"><a class="header-anchor" href="#hr\u53E3\u4E2D\u7684\u907F\u7A0E" aria-hidden="true">#</a> hr\u53E3\u4E2D\u7684\u907F\u7A0E</h2><h3 id="\u964D\u4F4E\u793E\u4FDD\u57FA\u6570" tabindex="-1"><a class="header-anchor" href="#\u964D\u4F4E\u793E\u4FDD\u57FA\u6570" aria-hidden="true">#</a> \u964D\u4F4E\u793E\u4FDD\u57FA\u6570</h3><p>\u901A\u8FC7\u4E0A\u8FB9\u7684\u4ECB\u7ECD\uFF0C\u6211\u60F3\u5927\u5BB6\u4E5F\u80FD\u770B\u660E\u767D\uFF0C\u5173\u4E8E\u201C\u4E94\u9669\u4E00\u91D1\u201D\u5176\u5B9E\u4F01\u4E1A\u9700\u8981\u627F\u62C5\u7684\u6210\u672C\u6BD4\u4E2A\u4EBA\u8981\u591A\u5F97\u591A\uFF0C\u4F46\u662F\u53D7\u76CA\u4EBA\u5374\u53EA\u6709\u6211\u4EEC\u6253\u5DE5\u4EBA</p><p>\u5728\u964D\u4F4E\u793E\u4FDD\u57FA\u6570\u7684\u60C5\u51B5\u4E0B\uFF0C\u867D\u7136\u80FD\u660E\u663E\u770B\u5230\u5230\u624B\u7684\u6536\u5165\u589E\u52A0\u4E86\uFF0C\u4F46\u662F\u90A3\u4EC5\u4EC5\u662F\u5230\u94F6\u884C\u5361\u7684\u5DE5\u8D44\u6570\u9AD8\u4E86\uFF0C\u201C\u4E94\u9669\u4E00\u91D1\u201D\u8D26\u6237\u91CC\u8FB9\u7684\u94B1\u5374\u5C11\u4E86\u66F4\u591A\uFF0C\u9644\u5E26\u7684\u4E00\u4E9B\u798F\u5229\u53EF\u80FD\u5C31\u6CA1\u6709\u4E86</p><p>\u4E0B\u9762\u53EF\u4EE5\u7B97\u4E00\u7B14\u8D26\uFF0C\u6211\u9762\u8BD5\u7684\u67D0\u5BB6\u516C\u53F8\u4E3A\u4F8B\uFF0C\u5047\u8BBE\u7A0E\u524D\u5DE5\u8D44\u662F20000\u4E07\u5143\u6574\uFF0C\u8FD9\u5BB6\u516C\u53F8\u7ED9\u51FA\u7684\u7F34\u8D39\u57FA\u6570\u662F2000\u5143\uFF0C\u4E14\u4E0D\u7F34\u7EB3\u516C\u79EF\u91D1\u3002 <br><br></p><p>\u5982\u679C\u6309\u7167\u8DB3\u989D\u7F34\u7EB3\uFF0C\u90A3\u4E48\u4E2A\u4EBA\u5DE5\u8D44\u6BCF\u6708\u5C06\u5C11\u53D14500\u5757\u94B1\u7684\u5DE5\u8D44\uFF0C\u540C\u65F6\u4F01\u4E1A\u9700\u8981\u591A\u7F34\u7EB37740\u5143</p><p>\u4E0D\u8003\u8651\u7A0E\u7684\u60C5\u51B5\u4E0B\uFF0C\u867D\u7136\u4E2A\u4EBA\u7684\u5230\u624B\u5DE5\u8D44\u5C11\u4E864500\u5757\u94B1\uFF0C\u4F46\u662F\u4EC5\u4EC5\u516C\u79EF\u91D1\u4E00\u9879\uFF0C\u8D26\u6237\u5373\u53EF\u591A4800\u5143</p><p><img src="https://mmbiz.qpic.cn/mmbiz_png/rguJGkWuUib0jhe2VNwu6pkQCAOAYJAEhIicaYGKcljVwWrqhmknw4EZbuRGElxBQR2WSMwU5YXj2NZjIvuL4AMA/640?wx_fmt=png&amp;wxfrom=5&amp;wx_lazy=1&amp;wx_co=1" alt="" loading="lazy"><br><br></p><p>\u4E0B\u9762\u6309\u7167\u8FD9\u5BB6\u516C\u53F8\u7684\u60F3\u6CD5\uFF0C\u7F34\u8D39\u57FA\u65702000\u5143\uFF08\u5317\u4EAC\u5E022021\u5E74\u7684\u7F34\u8D39\u57FA\u6570\u89C4\u5B9A\u662F5360\u5143\uFF0C\u4E0D\u6E05\u695A\u4ED6\u4EEC\u662F\u600E\u4E48\u8BBE\u7F6E\u76842000\u5143\uFF09\u3001\u4E0D\u7F34\u7EB3\u516C\u79EF\u91D1\u6765\u7B97</p><p>\u4F01\u4E1A\u7701\u4E867000\u591A\u5143\u7684\u6210\u672C\uFF0C\u4E2A\u4EBA\u5230\u624B\u7684\u5DE5\u8D44\u6536\u5165\u6BD4\u5168\u989D\u7F34\u7EB3\u591A\u4E86\u5DEE\u4E0D\u591A4000\u591A\u5757\u94B1\uFF0C\u770B\u8D77\u6765\u662F\u53CC\u8D62 <br><br></p><p>\u4F46\u662F\u5BF9\u4E8E\u4E2A\u4EBA\u5931\u53BB\u7684\u662F\u6BCF\u67084800\u5757\u94B1\u7684\u516C\u79EF\u91D1\u6536\u5165\uFF0C\u9644\u5E26\u7684\u5931\u53BB\u4E86\u4E70\u623F\u3001\u88C5\u4FEE\u516C\u79EF\u91D1\u8D37\u6B3E\u7684\u8D44\u683C\uFF1B</p><p>\u4EE5\u53CA\u6BCF\u6708\u4EC5\u4EC5480\u5143\u5230\u8D26\u7684\u517B\u8001\u4FDD\u9669\u8D26\u6237\uFF0C\u76F8\u8F83\u5168\u989D\u7F34\u7EB3\u6574\u6574\u5C11\u4E86\u8FD14500\u5143</p><p><img src="https://mmbiz.qpic.cn/mmbiz_png/rguJGkWuUib0jhe2VNwu6pkQCAOAYJAEhFKY3p3xaJZXKlpseexwfpX0L8xjH2Kk4HXeTxxrsrLwo0Ajk8gL0SA/640?wx_fmt=png&amp;wxfrom=5&amp;wx_lazy=1&amp;wx_co=1" alt="" loading="lazy"><br><br></p><p>\u5047\u8BBE\u8FD9\u5BB6\u516C\u53F8\u662F\u201C\u5EFA\u56FD\u4EE5\u6765\u611F\u52A8\u4E2D\u56FD\u597D\u4F01\u4E1A\u201D\uFF0C\u4ED6\u628A\u7701\u4E0B\u7684\u6210\u672C\u5168\u90FD\u7ED9\u4E86\u4E2A\u4EBA\uFF0C\u591A\u51FA\u6765\u76847000\u5757\u94B1\u4E5F\u62B5\u4E0D\u8FC7\u516C\u79EF\u91D1\u548C\u517B\u8001\u8D26\u6237\u7684\u91D1\u989D\uFF0C\u66F4\u4F55\u51B5\u8FD8\u635F\u5931\u4E86\u5176\u4ED6\u7684\u4E00\u4E9B\u798F\u5229 \u6240\u4EE5\u5404\u4F4D\u5DE5\u53CB\u6362\u5DE5\u4F5C\u7684\u65F6\u5019\uFF0C\u6211\u89C9\u5F97\u8FD9\u5E94\u8BE5\u4E5F\u662F\u9700\u8981\u8DDFhr\u804A\u6E05\u695A\u7684\u4E8B\u60C5</p><h3 id="\u5DE5\u8D44\u5206\u4E24\u4E2A\u94F6\u884C\u5361\u53D1" tabindex="-1"><a class="header-anchor" href="#\u5DE5\u8D44\u5206\u4E24\u4E2A\u94F6\u884C\u5361\u53D1" aria-hidden="true">#</a> \u5DE5\u8D44\u5206\u4E24\u4E2A\u94F6\u884C\u5361\u53D1</h3><p>\u9996\u5148\uFF0C\u5206\u4E24\u4E2A\u94F6\u884C\u5361\u53D1\uFF0C\u793E\u4FDD\u7684\u7F34\u8D39\u57FA\u6570\u5C31\u80AF\u5B9A\u4E0D\u662F\u5168\u989D\u7684\u4E86\uFF0C\u5177\u4F53\u635F\u5931\u60C5\u51B5\u53EF\u4EE5\u53C2\u8003\u4E0A\u4E00\u6761</p><p>\u5176\u6B21\uFF0C\u5206\u5F00\u53D1\u65E0\u975E\u4E5F\u662F\u4E3A\u4E86\u907F\u7A0E\uFF0C\u6211\u59CB\u7EC8\u8BA4\u4E3A\uFF0C\u5982\u679C\u8FD8\u662F\u516C\u53F8\u4E2D\u5C42\u4E00\u4E0B\u6253\u5DE5\u4EBA\u7684\u8EAB\u4EFD\uFF0C\u6211\u4EEC\u7684\u6536\u5165\u771F\u4E0D\u503C\u5F97\u6211\u4EEC\u5192\u7740\u8FDD\u6CD5\u9003\u7A0E\u7684\u98CE\u9669\u53BB\u7701\u8FD9\u70B9\u7A0E\u6B3E\uFF0C\u66F4\u4F55\u51B5\u73B0\u5728\u5E74\u5EA6\u6536\u5165\u7EDF\u4E00\u7ED3\u7B97\u5E76\u4E0D\u65AD\u4F18\u5316\u5347\u7EA7\u540E\uFF0C\u4F01\u4E1A\u7684\u8FD9\u4E9B\u624B\u6BB5\u80FD\u5426\u771F\u7684\u51D1\u6548\u8FD8\u6709\u5F85\u5546\u69B7\u5427</p><p>\u6700\u540E\uFF0C\u6B63\u5E38\u5DE5\u8D44\u5361\u7684\u6D41\u6C34\u5BF9\u4E8E\u518D\u6362\u5DE5\u4F5C\u8C08\u85AA\u53EF\u80FD\u4E5F\u4F1A\u4E0D\u5229</p><h3 id="\u53D1\u7968\u62A5\u9500\u7684\u65B9\u5F0F" tabindex="-1"><a class="header-anchor" href="#\u53D1\u7968\u62A5\u9500\u7684\u65B9\u5F0F" aria-hidden="true">#</a> \u53D1\u7968\u62A5\u9500\u7684\u65B9\u5F0F</h3><p>\u8FD9\u79CD\u60C5\u51B5\u9996\u5148\u4E5F\u4F1A\u964D\u4F4E\u793E\u4FDD\u7F34\u8D39\u57FA\u6570\uFF0C\u5177\u4F53\u53C2\u8003\u7B2C\u4E00\u6761</p><p>\u7136\u540E\u4F60\u8FD8\u5F97\u8D39\u52B2\u53BB\u51C6\u5907\u53D1\u7968\uFF0C\u6CA1\u90A3\u4E48\u591A\u53D1\u7968\u4F60\u53EF\u80FD\u4F1A\u901A\u8FC7\u4E00\u4E9B\u4E0D\u6B63\u89C4\u6E20\u9053\uFF0C\u4F01\u4E1A\u5C31\u8FD9\u4E48\u8F7B\u677E\u7684\u5C06\u98CE\u9669\u8F6C\u5AC1\u5230\u4E86\u4F60\u7684\u8EAB\u4E0A</p><p>\u6700\u91CD\u8981\u7684\u662F\uFF0C\u4E00\u65E6\u8DDF\u51FA\u73B0\u7EA0\u7EB7\uFF0C\u6D89\u53CA\u5230\u8D54\u507F\u7684\u95EE\u9898\uFF0C\u53EF\u80FD\u5C31\u4F1A\u5403\u4E86\u54D1\u5DF4\u4E8F\u4E86</p><h2 id="\u771F\u662F\u9047\u5230\u7684\u60C5\u51B5" tabindex="-1"><a class="header-anchor" href="#\u771F\u662F\u9047\u5230\u7684\u60C5\u51B5" aria-hidden="true">#</a> \u771F\u662F\u9047\u5230\u7684\u60C5\u51B5</h2><p>\u672C\u6765\u4E0D\u60F3\u8C08\u8FD9\u90E8\u5206\u5185\u5BB9\u7684\uFF0C\u7ED9\u4E00\u4E9B\u516C\u53F8\u7559\u70B9\u9762\u5B50\uFF0C\u4F46\u662F\u60F3\u7740\u540C\u662F\u5929\u6DAF\u6253\u5DE5\u4EBA\uFF0C\u76F8\u9022\u4F55\u5FC5\u66FE\u76F8\u8BC6\uFF0C\u5206\u4EAB\u51FA\u6765\u80FD\u7ED9\u5927\u5BB6\u4E00\u70B9\u53C2\u8003\u4E5F\u662F\u597D\u7684</p><p>\u4E11\u8BDD\u5148\u8BF4\u524D\u5934</p><p>1\u3001\u5929\u6D25\u91D1*\u8D44\u4EA7\u4EA4\u6613\u6240\uFF0C\u672C\u6765\u6211\u4EE5\u4E3A\u8FD9\u662F\u4E00\u5BB6\u6709\u653F\u5E9C\u80CC\u666F\u7684\u4F01\u4E1A\uFF0C\u5404\u9879\u798F\u5229\u5E94\u8BE5\u8FD8\u53EF\u4EE5\uFF0C\u6211\u975E\u5E38\u671F\u5F85\u7684\u51C6\u5907\u9762\u8BD5\uFF0C\u4E09\u8F6E\u6280\u672F\u9762\u540E\u5374\u8BA9\u6211\u975E\u5E38\u5931\u671B\u3002hr\u53F7\u79F0\u91C7\u7528\u963F\u91CC\u7684\u8003\u6838\u529E\u6CD5\uFF0C\u80FD\u63D0\u4F9B\u80FD\u529B\u548C\u804C\u4F4D\u7684\u664B\u5347\u7A7A\u95F4\uFF0C\u6536\u5165\u4E0A\u4F1A\u60F3\u529E\u6CD5\u907F\u7A0E\u3002\u8BD5\u7528\u671F\u516D\u4E2A\u6708\u5C31\u4E0D\u63D0\u4E86\uFF0C\u8BD5\u7528\u671F\u671F\u95F4\u53EA\u53D1\u4E00\u534A\u5DE5\u8D44\uFF0C\u8BF4\u662F\u53E6\u4E00\u534A\u7B49\u8FC7\u4E86\u8BD5\u7528\u671F\u518D\u53D1\uFF0C\u6240\u4EE5\u793E\u4FDD\u516C\u79EF\u91D1\u7684\u7F34\u8D39\u57FA\u6570\u4E5F\u5C31\u662F\u4E00\u534A\u5DE5\u8D44\u4E86\uFF0C\u90FD\u53EA\u53D1\u4E00\u534A\u5DE5\u8D44\u4E86\uFF0C\u8FD8\u6709\u5FC5\u8981\u907F\u7A0E\u5417\uFF1Fhr\u662F\u600E\u4E48\u8BF4\u51FA\u53E3\u7684\u3002\u540E\u6765\u6211\u67E5\u4E86\u4E00\u4E0B\uFF0C\u4ED6\u8FD8\u771F\u6709\u963F\u91CC\u7CFB\u7684\u80CC\u666F\uFF0C\u8682\u8681\u96C6\u56E2\u662F\u4ED6\u6700\u5927\u7684\u80A1\u4E1C\uFF0C\u8682\u8681\u96C6\u56E2\uFF0C\u9A6C\u4E91\u7684\u516C\u53F8\u5C31\u4E0D\u4E71\u8BC4\u8BBA\u4E86</p><p>2\u3001\u638C*\u5148\u673A\uFF0C\u6CA1\u9519\uFF0C\u5C31\u662F\u4E0D\u7BA1\u5B9A\u85AA\u591A\u5C11\uFF0C\u793E\u4FDD\u57FA\u6570\u5C31\u662F2000\u7684\u90A3\u4F4D\uFF0Chr\u8FD8\u4EA4\u5FC3\u7684\u8BF4\uFF1A\u516C\u53F8\u5904\u4E8EC\u8F6E\uFF0C\u9700\u8981\u5927\u5BB6\u4E00\u5757\u5E2E\u52A9\u516C\u53F8\u5EA6\u8FC7\u8FD9\u6BB5\u65F6\u95F4\uFF0C\u5C06\u6765\u53EF\u80FD\u6709\u671F\u6743\u80A1\u4EFD</p><p>\u6F02\u4EAE\u8BDD\u8BF4\u518D\u540E\u5934</p><p>3\u3001\u6DF1\u5733\u7684\u516C\u53F8\uFF0C\u6DF1\u4FE1\u670D\uFF0C\u5176\u5B9E\u9664\u4E86\u516C\u79EF\u91D1\u662F\u6309\u71675%\u7F34\u7EB3\uFF0C\u52A0\u73ED\u6BD4\u8F83\u591A\uFF0C\u5176\u4ED6\u90FD\u633A\u597D\uFF0C\u4E5F\u8BB8\u662F\u60F3\u5230\u4E86\u516C\u79EF\u91D1\u6BD4\u4F8B\u548C\u52A0\u73ED\u7684\u60C5\u51B5\uFF0Chr\u5728\u8981\u6C42\u7684\u85AA\u8D44\u57FA\u7840\u4E0A\u63D0\u4E86\u5DEE\u4E0D\u591A30%\uFF0C\u5E76\u4E14\u52A0\u73ED\u548C\u516C\u79EF\u91D1\u7F34\u7EB3\u6BD4\u4F8B\u8FD9\u4E2A\u4E8B\u60C5hr\u662F\u4E3B\u52A8\u5148\u63D0\u51FA\u6765\u7684\uFF0C\u4E5F\u662F\u516C\u53F8\u89C4\u5B9A\uFF0C\u6240\u4EE5\u90FD\u63D0\u524D\u6C9F\u901A\u597D\u4E86\u8BA9\u5750\u9009\u62E9</p><p>4\u3001\u9886\u521B\u667A\u4FE1\uFF0C\u4E00\u4E2A\u5916\u4F01\u80CC\u666F\u7684\u516C\u53F8\uFF0C\u5F53\u65F6\u662F\u6211\u610F\u5411\u6700\u6DF1\u7684\u4E00\u5BB6\uFF0C\u4E5F\u662F\u9762\u8BD5\u51C6\u5907\u6700\u5145\u5206\u7684\uFF0C\u56DB\u8F6E\u6280\u672F\u9762+1\u8F6EHR\u9762\uFF0C\u85AA\u8D44\u4E5F\u6BD4\u8F83\u820D\u5F97\u7ED9\uFF0C\u6CA1\u52A0\u73ED\uFF0C\u798F\u5229\u548C\u5DE5\u4F5C\u73AF\u5883\u4E5F\u4E0D\u9519\uFF0CHR\u4EBA\u4E5F\u7279\u522B\u597D\uFF0C\u4F46\u662F\u56E0\u4E3A\u4E00\u4E9B\u4E2A\u4EBA\u539F\u56E0\u6700\u7EC8\u62D2\u7EDD\u4E86offer\uFF0C\u8FD8\u662F\u633A\u9057\u61BE\u7684</p><p>\u751F\u6D3B\u4E0D\u6613\uFF0C\u6253\u5DE5\u66F4\u4E0D\u6613\u3002\u968F\u7740\u5404\u5927\u4E92\u8054\u7F51\u516C\u53F8\u53D6\u6D88996\u8D8B\u52BF\uFF0C\u65B0\u4E00\u4EE3\u6C11\u5DE5\u4E5F\u8981\u591A\u4E3A\u81EA\u5DF1\u7684\u751F\u6D3B\u548C\u672A\u6765\u7EFC\u5408\u8003\u8651\u5DE5\u4F5C\uFF0C\u8D70\u53EF\u6301\u7EED\u53D1\u5C55\u7684\u9053\u8DEF</p><div style="text-align:right;"><p>\u6587/\u6234\u5148\u751F@2021\u5E749\u670812\u65E5</p></div>',56),d=[i];function n(t,c){return e(),r("div",null,d)}var l=a